<template>
  <div class="table q-ml-lg q-mr-lg">
    <div class="settings-area q-mb-sm">
      <div class="radio-area">
        View users whose dato is:
        <br />
        <q-radio
          class="q-mx-sm radio"
          v-model="viewingPair"
          val="even"
          label="Even"
          @input="getPairs('even')"
        />
        <q-radio
          class="q-mx-sm radio"
          v-model="viewingPair"
          val="odd"
          label="Odd"
          @input="getPairs('odd')"
        />
        <q-radio
          class="q-mx-sm radio"
          v-model="viewingPair"
          val="any"
          label="Any"
          @input="getPairs('any')"
        />
      </div>
      <div class="button-area">
        <q-btn unelevated rounded class="color-primary " @click="toggleGrid"
          >View as a {{ viewingMode }}</q-btn
        >
      </div>
    </div>
    <q-dialog v-model="viewingModal" medium>
      <q-card>
        <h3
          class="color-primary q-mx-xl q-mt-xl text-center font-weight-500"
          v-if="!selectedRows.length"
        >
          You must select at least one item from the main table to preview your
          results here.
        </h3>
        <q-table v-else :data="selectedRows" :columns="columns" />
        <q-card-actions align="right" class="bg-white text-teal">
          <q-btn
            flat
            rounded
            color="primary"
            label="Close Preview"
            v-close-popup
          />
        </q-card-actions>
      </q-card>
    </q-dialog>
    <q-btn
      @click="viewingModal = !viewingModal"
      flat
      rounded
      color="primary"
      label="Preview your selected options as a table"
      class="q-mb-md"
    />

    <q-skeleton v-if="!hasLoadedData" animation="wave" height="60vh" />
    <q-table
      v-else
      :pagination="tablePagination"
      :data="tableData"
      :columns="columns"
      row-key="id"
      :grid="isGrid"
      selection="multiple"
      :selected.sync="selectedRows"
    />
    <q-btn
      unelevated
      color="primary"
      label="Download Invoice"
      class="q-ma-lg float-right"
      @click="downloadPDF"
    />
  </div>
</template>

<script>
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import faker from 'faker';
import { date } from 'quasar';
import json from '../assets/TEST_DATA';

export default {
  name: 'Table',
  components: {},
  methods: {
    toggleGrid() {
      this.isGrid = !this.isGrid;
      this.viewingMode = this.viewingMode === 'grid' ? 'table' : 'grid';
    },
    isOdd(num) {
      return num % 2 === 1;
    },
    getPairs(numberType) {
      // 1) Every time that we switch to odd/even/any data, reset the selected rows
      this.selectedRows = [];
      this.tableData = [];

      // 2) Push only the items with the type of dato we're interested in (odd/even/data);
      json.forEach((item) => {
        if (
          (numberType === 'odd' && this.isOdd(item.dato)) ||
          (numberType === 'even' && !this.isOdd(item.dato)) ||
          numberType === 'any'
        ) {
          this.tableData.push(item);
        }
      });
    },
    generatePDFTable() {
      // 1) Define an empty array where we'll store our table row data
      const cellsToAppend = [];

      // 2) Push the text from all selected row items to our array
      this.selectedRows.forEach((item) => {
        cellsToAppend.push([
          { text: item.id, margin: [0, 15, 0, 0] },
          { text: item.first_name, margin: [0, 15, 0, 0] },
          { text: item.last_name, margin: [0, 15, 0, 0] },
          { text: item.email, margin: [0, 15, 0, 0] },
          { text: item.dato, margin: [0, 15, 0, 0] }
        ]);
      });

      // 3) Set table body as the columns name inside an array
      this.tableBody = [this.columnsName];

      // 4) At this point, cellsToAppend is an array of arrays, we'll append its
      // arrays to tableBody.
      cellsToAppend.forEach((row) => {
        this.tableBody.push(row);
      });
    },
    downloadPDF() {
      // 1) If there are zero rows selected, display a dialog indicating so
      if (!this.selectedRows.length) {
        return this.$q.dialog({
          title: 'About that...',
          message:
            'You must select at least one user to download their invoice/s.'
        });
      }

      // 2) Generate the PDF Table body
      this.generatePDFTable();

      // 3) Define a date for the invoice
      const timeStamp = Date.now();
      const formattedString = date.formatDate(timeStamp, 'D-MM-YYYY');

      // 4) Load the PDF fonts
      pdfMake.vfs = pdfFonts.pdfMake.vfs;

      // 5) Create the PDF body
      const docDefinition = {
        content: [
          { text: 'Nombre de Factura', style: 'header' },
          {
            columns: [
              {
                width: '60%',
                style: 'textSmall',
                text: `
            Número de facturación: ${faker.finance.account()}
            Fecha de facturación: ${formattedString}
            ID de cliente: ${faker.random.number()}
            ID de producto: AAH-23883994
            ID de empresa: ${faker.random.number()}
            ${faker.address.streetAddress()}
            `
              },
              {
                width: 200,
                image:
                  'data:image/jpeg;base64,'
              }
            ]
          },
          {
            columns: [
              {
                width: '50%',
                style: 'header2',
                text: 'Dirección de facturación:'
              },
              {
                width: '50%',
                style: 'header2',
                text: 'Dirección de entrega:'
              }
            ]
          },
          {
            columns: [
              {
                width: '50%',
                style: 'textSmall2',
                text: `
                ${faker.address.streetAddress()}
                ${faker.address.secondaryAddress()}
                ${faker.address.state()}
                ${faker.address.country()}
                `
              },
              {
                width: 'auto',
                style: 'textSmall2',
                text: `
                ${faker.address.streetAddress()}
                ${faker.address.secondaryAddress()}
                ${faker.address.county()}
                `
              }
            ]
          },
          {
            canvas: [
              {
                type: 'line',
                x1: 0,
                y1: 10,
                x2: 510,
                y2: 10,
                lineWidth: 2
              }
            ]
          },
          {
            columns: [
              {
                width: '60%',
                style: 'textSmall',
                text: `
                Dato número 1:          38593 49959 29944 -433
                Dato número 2:          25622456
                Dato número 3:          HU G6.034
                `
              }
            ]
          },
          {
            text:
              'Wir danken Ihnen für Ihre Anfrage und erstellen Ihnen folgendes Angebot:',
            style: 'textMd'
          },
          {
            layout: 'lightHorizontalLines',
            table: {
              headerRows: 1,
              fillColor: '#dedede',
              // Set as many widths as columns you have
              widths: ['20%', '20%', '20%', '20%', '20%'],
              body: this.tableBody
            }
          }
        ],

        styles: {
          filledHeader: {
            bold: true,
            fontSize: 14,
            color: 'white',
            fillColor: '#004457',
            fillOpacity: '#333333',
            alignment: 'left'
          },
          textSmall: {
            fontSize: 10,
            lineHeight: 2
          },
          textSmall2: {
            fontSize: 10
          },
          textMd: {
            fontSize: 14,
            lineHeight: 2
          },
          header: {
            fontSize: 22,
            bold: true
          },
          header2: {
            fontSize: 18,
            bold: true
          }
        }
      };

      // 6) Download the PDF
      pdfMake.createPdf(docDefinition).download();
    }
  },
  data() {
    return {
      tableBody: [],
      viewingModal: false,
      isGrid: false,
      hasLoadedData: false,
      viewingMode: 'grid',
      viewingPair: 'any',
      selectedRows: [],
      tablePagination: {
        sortBy: 'dato',
        descending: true,
        page: 1,
        rowsPerPage: 10
      },
      columns: [
        {
          name: 'id',
          required: true,
          label: 'User ID',
          align: 'left',
          field: 'id',
          format: (val) => `${val}`,
          sortable: true,
          headerClasses: 'bg-primary text-white'
        },
        {
          name: 'first_name',
          align: 'center',
          label: 'First Name',
          field: 'first_name',
          sortable: true
        },
        {
          name: 'last_name',
          label: 'Last Name',
          field: 'last_name',
          sortable: true
        },
        { name: 'email', label: 'Email', field: 'email' },
        {
          name: 'dato',
          label: 'Dato',
          field: 'dato',
          sortable: true
        }
      ],
      columnsName: [],
      tableData: []
    };
  },
  mounted() {
    // 1) Set the table data as the json coming from our mock data file
    this.tableData = json;
    this.hasLoadedData = true;

    // 2) Fill the columns name data. This array will be used to fill the PDF's table header
    this.columnsName = this.columns.map((item) => ({
      text: item.label,
      style: 'filledHeader',
      // 3) Add a little bit of padding inside the cells using its own margin
      margin: [0, 15, 0, 0]
    }));
  }
};
</script>

<style lang="scss">
.settings-area {
  display: flex;
  align-items: center;
  justify-content: space-between;

  @media only screen and (max-width: $breakpoint-xs-max) {
    flex-direction: column;
    align-items: flex-start;
  }
}
</style>
